package edu;

public class Test10_부정연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//변수에 직접 Not 연산자 사용하는 예
		boolean isOn = true; // 변수 isOn은 참이다
		isOn = !isOn;		// isOn 변수에 Not isOn 값을 넣어라 --> Not true값을 넣어라
		System.out.println(isOn); // false 출력
		
		//조건식에서 Not 연산자 사용하는 예
		//제어문(조건문)0. 파트 참고
		
	}

}
